
public class Loginpojo {
	private String lmail;
	private String lpwd;
	public Loginpojo() {
		super();
		// TODO Auto-generated constructor stub
	}
	public Loginpojo(String lmail, String lpwd) {
		super();
		this.lmail = lmail;
		this.lpwd = lpwd;
	}
	public String getLmail() {
		return lmail;
	}
	public void setLmail(String lmail) {
		this.lmail = lmail;
	}
	public String getLpwd() {
		return lpwd;
	}
	public void setLpwd(String lpwd) {
		this.lpwd = lpwd;
	}
	
	

}
